$(function() {
    $(".content__type>span,.toolbar ul>li").on("click", function() {
        $(this)
            .addClass("active")
            .siblings()
            .removeClass("active");

    });
    setInterval(function() {
        src = $('.slider .active img').attr('src');
        $('.slider__container').css('background-image', 'url(' + src + ')');

        console.log(src);
    }, 3000);

});
//getAll Image Slider
let slider = document.querySelectorAll('.slider .slider__content'),
    sliderArr = Array.from(slider);
let sliderLength = sliderArr.length;
console.log(sliderLength)
    //Creat Bullets
let currentSlide = 1;
theSlider();

setInterval(function() {
    if (currentSlide != sliderLength) {
        nextFunction();

        theSlider();
    } else {
        return currentSlide = 1;
    }
}, 3000);


function removeActiveImg() {
    slider.forEach(function(img) {
        img.classList.remove('active');
    })
}
nextBtn.onclick = nextFunction;
prevBtn.onclick = prevFunction;

function nextFunction() {
    if (nextBtn.classList.contains('disabled')) {
        return false;
    } else {

        currentSlide++;
        theSlider();
    }


};

function prevFunction() {
    if (prevBtn.classList.contains('disabled')) {
        return false;
    } else {
        currentSlide--;
        theSlider();

    }


}
//The slider Function 
function theSlider() {
    removeActiveImg();
    slider[currentSlide - 1].classList.add('active');
    if (currentSlide == 1) {
        prevBtn.classList.add('disabled');
    } else {
        prevBtn.classList.remove('disabled');

    };
    if (currentSlide == sliderLength) {
        nextBtn.classList.add('disabled');
    } else {
        nextBtn.classList.remove('disabled');


    }

}